import React,{useEffect, useState} from 'react';
import axios from "axios";
import { useNavigate, useParams } from 'react-router-dom';

const EditProduct = () => {
    const [nama_barang, setNamaBarang] = useState("");
    const [jenis_barang, setJenisBarang] = useState("");
    const [jumlah, setJumlah] = useState("");
    const [pemilik_barang, setPemilikBarang] = useState("");
    const [tgl_masuk, setTglMasuk] = useState("");
    const [tgl_keluar, setTglKeluar] = useState("");
    const [kode_rak, setKodeRak] = useState("");
    const navigate = useNavigate();
    const {id} = useParams();

useEffect(() => {
  getProductById();
}, [])

    const updateProduct = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:5000/products", {
                nama_barang,
                jenis_barang,
                jumlah,
                pemilik_barang,
                tgl_masuk,
                tgl_keluar,
                kode_rak
          });
          navigate("/Products");
        } catch (error) {
          console.log(error);
        }
      }

      const getProductById = async () =>{
        const response = await axios.get(`http://localhost:5000/products/${id}`);
        setNamaBarang(response.data.nama_barang);
        setJenisBarang(response.data.jenis_barang);
        setJumlah(response.data.jumlah);
        setPemilikBarang(response.data.pemilik_barang);
        setTglMasuk(response.data.tgl_masuk);
        setTglKeluar(response.data.tgl_keluar);
        setKodeRak(response.data.kode_rak);
    }
      
  return (
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <form onSubmit={updateProduct}>
          <div className="field">
            <label className="label">Nama Barang</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={nama_barang}
                onChange={(e) => setNamaBarang(e.target.value)}
                placeholder="Nama Barang"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Jenis Barang</label>
            <div className="control">
              <input
                type="text"
                className="input"
                value={jenis_barang}
                onChange={(e) => setJenisBarang(e.target.value)}
                placeholder="Jenis Barang"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Jumlah</label>
            <div className="control">
                <input
                type='text'
                className='input'
                value={jumlah}
                onChange={(e) => setJumlah(e.target.value)}
                placeholder='Jumlah'
                />
            </div>
          </div>
          <div className="field">
            <label className="label">Pemilik Barang</label>
            <div className="control">
                <input
                type='text'
                className='input'
                value={pemilik_barang}
                onChange={(e) => setPemilikBarang(e.target.value)}
                placeholder='Pemilik Barang'
                />
            </div>
          </div>
          <div className="field">
            <label className="label">Tanggal Masuk</label>
            <div className="control">
                <input
                type='text'
                className='input'
                value={tgl_masuk}
                onChange={(e) => setTglMasuk(e.target.value)}
                placeholder='dd-mm-yyyy'
                />
            </div>
          </div>
          <div className="field">
            <label className="label">Tanggal Keluar</label>
            <div className="control">
                <input
                type='text'
                className='input'
                value={tgl_keluar}
                onChange={(e) => setTglKeluar(e.target.value)}
                placeholder='Tanggal Keluar'
                />
            </div>
          </div>
          <div className="field">
            <label className="label">Kode Rak</label>
            <div className="control">
                <input
                type='text'
                className='input'
                value={kode_rak}
                onChange={(e) => setKodeRak(e.target.value)}
                placeholder='Kode Rak'
                />
            </div>
          </div>
          <div className="field">
            <button type="submit" className="button is-success">
              Save
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};


export default EditProduct;
