import React,{useState, useEffect} from 'react';
import axios from "axios";
import { useNavigate } from 'react-router-dom';

const AddUser = () => {
const [nama_penyewa, setNamaPenyewa] = useState("");
const [alamat, setAlamat] = useState("");
const [tgl_sewa, setTglSewa] = useState("");
const [durasi, setDurasi] = useState();
const [lokasi_ambil, setLokasiAmbil] = useState("");
const [lokasi_kembali, setLokasiKembali] = useState("");
const [email, setEmail] = useState("");
const [no_telp, setNoTelp] = useState("");
const [totalHarga, setTotalHarga] = useState();
const [mobId, setMobId] = useState();
const [Mobil, setMobil] = useState([]);
const navigate = useNavigate();

const saveUser = async (e) => {
    e.preventDefault();
    try {
        await axios.post("http://localhost:5000/users", {
          nama_penyewa: nama_penyewa,
          alamat: alamat,
          tgl_sewa: tgl_sewa,
          durasi: durasi,
          lokasi_ambil: lokasi_ambil,
          lokasi_kembali: lokasi_kembali,
          email: email,
          no_telp: no_telp,
          totalHarga: totalHarga,
          mobId: mobId
      });
      navigate("/UserList");
    } catch (error) {
      console.log(error);
    }
  };

  const notif = () => {
    alert("Selamat Pesanan Anda Berhasil Di Buat!");
  }

  useEffect(() => {
    getMobil();
  }, []);

  const getMobil = async () => {
    const response = await axios.get("http://localhost:5000/mobil");
    setMobil(response.data);
  };

  // Calculate totalHarga when mobId or durasi changes
  useEffect(() => {
    if (mobId && durasi) {
      const selectedMobil = Mobil.find((mobil) => mobil.id === mobId);
      const hargaPerHari = selectedMobil ? selectedMobil.harga : 0;
      const calculatedTotalHarga = hargaPerHari * durasi;
      setTotalHarga(calculatedTotalHarga);
    }
  }, [mobId, durasi, Mobil]);

  return (
    <div className="container">
    <div className="columns mt-5 is-centered">
      <div className="column is-half">
        <h1 className='title'>Silahkan Masukkan Data Diri Anda</h1>
        <form onSubmit={saveUser}>
          <div className="field">
            <label className="label">Nama Lengkap</label>
            <div className="control">
              <input
                type="text"
                className="input"
                name='nama'
                value={nama_penyewa}
                onChange={(e) => setNamaPenyewa(e.target.value)}
                placeholder="Nama Lengkap"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Alamat</label>
            <div className="control">
              <input
                type='text'
                className="input"
                name='alamat'
                value={alamat}
                onChange={(e) => setAlamat(e.target.value)}
                placeholder="JL. xxx"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Tanggal Sewa</label>
            <div className="control">
              <input
                type="date"
                className="input"
                name='tanggal-sewa'
                value={tgl_sewa}
                onChange={(e) => setTglSewa(e.target.value)}
                placeholder="dd-mm-yyyy"
              />
            </div>
          </div>
          <div className="field">
            <div className="control">
              <div className="select is-fullwidth">
                <label className="form-label">List Mobil</label>
                  <select className="form-control w-25"
                    name='mobil'
                    value={mobId}
                    onChange={(e) => setMobId(e.target.value)}
                    >
                    <option value="">--Silahkan Pilih Mobil--</option>
                    {Mobil.map((mobil) => (
                      <React.Fragment key={mobil.id}>
                      <option value={mobil.id}>{mobil.nama} Harga : Rp.{mobil.harga * (durasi || 1)}</option>
                      </React.Fragment>
                      ))}
                  </select>
              </div>
            </div>
          </div>
          <div className="field">
            <label className="label">Durasi</label>
            <div className="control">
              <input
                type="number"
                className="input"
                name='durasi'
                value={durasi}
                onChange={(e) => setDurasi(e.target.value)}
                placeholder="1"
              /> Hari
            </div>
          </div>
          <div className="field">
            <label className="label">Lokasi Pengambilan</label>
            <div className="control">
              <input
                type="text"
                className="input"
                name='lokasi-pengambilan'
                value={lokasi_ambil}
                onChange={(e) => setLokasiAmbil(e.target.value)}
                placeholder="Tempat Ambil"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Lokasi Pengembalian</label>
            <div className="control">
              <input
                type="text"
                className="input"
                name='lokasi-pengembalian'
                value={lokasi_kembali}
                onChange={(e) => setLokasiKembali(e.target.value)}
                placeholder="Tempat Kembali"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Email</label>
            <div className="control">
              <input
                type="email"
                className="input"
                name='email'
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Email"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">No Telfon</label>
            <div className="control">
              <input
                type="text"
                className="input"
                name='no-telp'
                value={no_telp}
                onChange={(e) => setNoTelp(e.target.value)}
                placeholder="08xxxxxxx"
              />
            </div>
          </div>

        <div className="field">
          <label className="label">Total Harga</label>
            <div className="control">
              <input
                type="text"
                className="input"
                name="totalHarga"
                value={totalHarga}
                readOnly
              />
        </div>
      </div>

          <div className="field">
            <button type="submit" onClick={notif} className="button is-success is-fullwidth">
              Buat Pesanan
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
  );
};


export default AddUser;
