import React, {useState, useEffect} from 'react';
import axios from "axios";
import {Link} from "react-router-dom";

const Products = () => {
    const [barang, setBarang] = useState([]);

useEffect(()=> {
  getProducts();
}, []);

const getProducts = async () => {
const response = await axios.get("http://localhost:5000/products");
  setBarang(response.data);
};

const deleteProduct = async (id) => {
  try {
    await axios.delete(`http://localhost:5000/products/${id}`);
    getProducts();
  } catch (error) {
      console.log(error);
  }
}


  return (
    <div className="columns mt-5 is-centered">
        <div className="column is-three-quarters">
        <Link to={`AddProduct`} className="button is-success">
          Tambahkan Barang
          </Link>
            <table className='table is-striped is-fullwidth'>
              <thead>
                <tr>
                  <th>No</th>
                  <th>Nama Barang</th>
                  <th>Jenis Barang</th>
                  <th>Jumlah</th>
                  <th>Pemilik Barang</th>
                  <th>Tanggal Masuk</th>
                  <th>Tanggal Keluar</th>
                  <th>Kode Rak</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
              {barang.map((product, index)=> (
                 <tr key ={product.id}>
                 <td>{index + 1 }</td>
                 <td>{product.nama_barang}</td>
                 <td>{product.jenis_barang}</td>
                 <td>{product.jumlah}</td>
                 <td>{product.pemilik_barang}</td>
                 <td>{product.tgl_masuk}</td>
                 <td>{product.tgl_keluar}</td>
                 <td>{product.kode_rak}</td>
                 <td>
                    <Link 
                    to={`editproducts/${product.id}`} 
                    className="button is-small is-info">Edit
                    </Link>
                    <button onClick={ () => deleteProduct(product.id)}className="button is-small is-danger">Delete</button>
                 </td>
                 <td></td>
               </tr>   
                ))}
              </tbody>
            </table>
        </div>
    </div>
  )
}

export default Products
